CREATE TABLE invites(invite_id int not null auto_increment, event_id bigint not null, event_type_id bigint not null, attendee_id bigint not null, invite_narration varchar(255) not null, date_created timestamp default current_timestamp, date_updated timestamp default current_timestamp on update current_timestamp, foreign key (invite_id), constraint FK_InvitesAndEvents foreign key (event_id) references magniva_events(event_id), constraint FK_InvitesAndInviteTypes foreign key (event_type_id) references invite_types(type_id));
